import React from "react";
import io from "socket.io-client";
import View from "./View.js";
export default class Chat extends React.Component {
  constructor(props) {
    super(props);
    this.socket = null;
    this.state = {
      messages: [],
      systemMessage: "",
      userName: "",
      nickName: ""
    };
  }

  initiateConnection = () => {
    this.socket = io.connect("http://localhost:8080");
  };

  initiateMessageListener = () => {
    this.socket.on("chat message", msg => {
      console.log("msg", msg.message);
      this.setState({
        messages: [...this.state.messages, msg.message]
      });
    });
  };

  initiateNewJoinListener = () => {
    this.socket.on("newly joined", data => {
      console.log("data", data.message);
      this.setState({
        systemMessage: data.message
      });
    });
  };

  sendMessage = () => {
    this.socket.emit("chat message", "Button has been clicked");
  };

  componentDidMount() {
    this.initiateConnection();
    this.initiateMessageListener();
    this.initiateNewJoinListener();
  }

  render() {
    return (
      <div>
        <h1>Welcome to RatChat. Chat app for Ratchet ppl</h1>
        <p id="systemMessage">{this.state.systemMessage}</p>
        {this.state.messages.map(el => (
          <p>{el}</p>
        ))}
        <button onClick={this.sendMessage}>Send Button !</button>
        <button onClick={this.props.logout}>Log out!</button>

        <View messages={this.state.messages} />
      </div>
    );
  }
}
