import React from "react";
import logo from "./logo.svg";
import io from "socket.io-client";
import "./App.css";

class App extends React.Component {
  constructor() {
    super();
    this.socket = null;
    this.state = {
      userName: "",
      systemMessage: "",
      messageText: "",
      messages: []
    };
  }

  initiateConnection = () => {
    this.socket = io.connect("http://localhost:8080");
  };

  initiateMessageListener = () => {
    this.socket.on("chat message", msg => {
      console.log("msg", msg.message);
      this.setState({
        messages: [...msg.message]
      });
    });
  };

  initiateNewJoinListener = () => {
    this.socket.on("newly joined", data => {
      console.log("data", data.message);
      this.state.messages.push(data.message);
      this.setState({
        systemMessage: this.state.messages.push(data.message)
      });
    });
  };

  componentDidMount() {
    this.initiateConnection();
    this.initiateMessageListener();
    this.initiateNewJoinListener();
  }

  render() {
    console.log("state", this.state);
    return (
      <div className="App">
        <p id="systemMessage">{this.state.systemMessage}</p>
      </div>
    );
  }
}

export default App;
