import React from "react";
import logo from "./logo.svg";
import io from "socket.io-client";
import "./App.css";
import fire from "./config/Fire.js";
import Login from "./components/Login/Login.js";
import { FadeLoader } from "react-spinners";
import Chat from "./components/Chat/Chat.js";

class App extends React.Component {
  constructor() {
    super();
    this.socket = null;
    this.state = {
      userName: "",
      systemMessage: "",
      messageText: "",
      messages: [],
      user: null,
      loaded: false
    };
  }

  initiateConnection = () => {
    this.socket = io.connect("http://localhost:8080");
  };

  initiateMessageListener = () => {
    this.socket.on("chat message", msg => {
      console.log("msg", msg.message);
      this.setState({
        messages: [...this.state.messages, msg.message]
      });
    });
  };

  initiateNewJoinListener = () => {
    this.socket.on("newly joined", data => {
      console.log("data", data.message);
      this.setState({
        systemMessage: data.message
      });
    });
  };

  logout = () => {
    fire.auth().signOut();
  };

  authListener() {
    fire.auth().onAuthStateChanged(user => {
      if (user) {
        console.log("logged in success");
        this.setState({
          user: user,
          loaded: true
        });
        // this.getUserInfo(user);
        localStorage.setItem("user", user.uid);
      } else {
        this.setState({
          user: null
        });
        localStorage.removeItem("user");
      }
    });
  }

  getUserInfo = user => {
    console.log("app user", user);
    this.setState({
      user: user,
      loaded: true
    });
  };

  componentDidMount() {
    this.initiateConnection();
    this.initiateMessageListener();
    this.initiateNewJoinListener();
    this.authListener();
  }

  displayLogin = () => {
    if (this.state.user) {
      return <Chat />;
    } else if (!this.state.loaded) {
      return (
        <>
          <FadeLoader
            sizeUnit={"px"}
            size={500}
            color={"#123abc"}
            loading={!this.state.loaded}
          />{" "}
        </>
      );
    } else if (!this.state.user) {
      return (
        <>
          <Login getUserInfo={this.getUserInfo} />
        </>
      );
    }
  };

  render() {
    console.log("state", this.state);
    return (
      <div className="App">
        <p>Welcome to RatChat</p>
        {this.state.loaded ? (
          <>{this.displayLogin()}</>
        ) : (
          <FadeLoader
            sizeUnit={"px"}
            size={500}
            color={"#123abc"}
            loading={!this.state.loaded}
          />
        )}
      </div>
    );
  }
}

export default App;
